// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract ShippingContract {

  // Status du contrat
  enum Status {Pending, Shipped, Delivered}
  // Adresse du vendeur
  address public owner;

  // Adresse de l'acheteur
  address public buyer;

  /// Compteur de commande
  uint compteurCommande;
  
  // Structure de l'acheteur
  struct Buyer{
      address addr;
      string name;
      bool init;
  }

  // Structure de la commande
  struct Order{
    string produit;
    uint number;
  }

   constructor (address _buyerAddr) public payable {
        owner = msg.sender;
        buyer = _buyerAddr;
    }

  /// The mapping to store orders
  mapping (uint => Order) orders;


  modifier restricted() {
    require(
      msg.sender == owner,
      "This function is restricted to the contract's owner"
    );
    _;
  }
   
  /// Event triggered for every new order
  event OrderSent(address buyer, string goods, uint quantity, uint orderno);

}
